define([], function() { return "import * as React from 'react';\nimport {\n  Checkbox\n  } from '../../../../Checkbox';\n\nexport interface ICheckboxBasicExampleState {\n  isChecked: boolean;\n}\n\nexport class CheckboxBasicExample extends React.Component<{}, ICheckboxBasicExampleState> {\n  constructor() {\n    super();\n\n    this.state = {\n      isChecked: false\n    };\n\n    this._onCheckboxChange = this._onCheckboxChange.bind(this);\n  }\n\n  public render() {\n    let { isChecked } = this.state;\n\n    return (\n      <div>\n        <Checkbox\n          label='Uncontrolled checkbox'\n          onChange={ this._onCheckboxChange } />\n\n        <Checkbox\n          label='Uncontrolled checkbox with defaultChecked true'\n          defaultChecked={ true }\n          onChange={ this._onCheckboxChange } />\n\n        <Checkbox\n          label='Disabled uncontrolled checkbox with defaultChecked true'\n          disabled={ true }\n          defaultChecked={ true }\n          onChange={ this._onCheckboxChange } />\n\n        <Checkbox\n          label='Controlled checkbox'\n          checked={ isChecked }\n          onChange={ (ev, checked) => this.setState({ isChecked: checked }) } />\n      </div>\n    );\n  }\n\n  private _onCheckboxChange(ev: React.FormEvent, isChecked: boolean) {\n    console.log(`The option has been changed to ${ isChecked }.`);\n  }\n\n}\n"; });